import React, { Component } from 'react';
import Header from './Header';
import Formulario from './Formulario';
import {obtenerDiferenciaAnio, calcularMarca} from '../Helper';

class App extends Component {
  cotizarSeguros = (datos) => {
    const {marca, plan, year} = datos;

    //agregar base del seguro
    let resultado = 2000;

    // Obtener la diferencia de a単o
    const diferencia = obtenerDiferenciaAnio(year);

    //por cada a単o restar el 3% del valor del auto
    resultado -= ((diferencia * 3) * resultado) / 100;


    //Americano 15% asiatico 5% y europeo30% de incremento al valor actual
    resultado = calcularMarca(marca) * resultado;




  }
  render() {
    return (
      <div className="contenedor">
        <Header 
          titulo = 'Cotizador de Seguros de Autos'
        />

        <div className="contenedor-formulario">
          <Formulario 
            cotizarSeguros = {this.cotizarSeguros}
          />
        </div>
      </div>
    );
  }
}

export default App;
